'''КЛАССНАЯ'''num_1 = int(input("Введите число"))num_2 = int(input("Введите второе число"))print(num_1 + num_2)print(num_1 - num_2)print(num_1 * num_2)print(num_1 / num_2)'''ДОМАШНЯЯ'''num_1 = int(input('Введите число'))num_2 = int(input('Введите второе число'))num_3 = int(input('Введите третье число'))num_4 = int(input('Введите четвертое число'))print(num_1 + num_2 + num_3 + num_4)print(num_1 - num_2 - num_3 - num_4)print(num_1 * num_2 * num_3 * num_4)print(num_1 / num_2 / num_3 / num_4)